// Utility: safely escape regex special characters
function escapeRegex(str) {
  return (str || '').replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

// Safe extraction function
function extractSection(text, sectionName) {
  if (!sectionName) return ''; // Avoid empty patterns
  const safeSection = escapeRegex(sectionName);

  // Build regex carefully; prevent "nothing to repeat" by anchoring pattern
  const pattern = `${safeSection}[)\\s:\\*\\-]*([\\s\\S]*?)(?=\\n\\d+\\)|\\n[A-Z]|$)`;
  const regex = new RegExp(pattern, 'i');

  const match = text.match(regex);
  return match ? match[1].trim() : '';
}

// Extraction logic (use exact section headers)
