const businessDesc = $('Parse Research Data').first().json.business_description;
const coreProblems = $('Parse Research Data').first().json.core_problems;

// Simple markdown-to-plain-text function
function mdToText(md) {
  if (!md) return '';
  return md
    .replace(/```[\s\S]*?```/g, '')        // remove code blocks
    .replace(/`([^`]*)`/g, '$1')           // remove inline code marks
    .replace(/\[([^\]]+)\]\([^)]+\)/g, '$1') // convert markdown links to text
    .replace(/(\*\*|__|\*|_)/g, '')        // remove bold/italic
    .replace(/^> /gm, '')                  // remove blockquotes
    .replace(/^#+ /gm, '')                 // remove headings
    .replace(/^[\*\-\+]\s+/gm, '')         // remove list bullets
    .replace(/\n{2,}/g, '\n')              // normalize extra spacing
    .trim();
}

// Convert all
const plain1 = mdToText(missionVision);
const plain2 = mdToText(valueProp);
const plain3 = mdToText(businessDesc);
const plain4 = mdToText(coreProblems);

// Return all as JSON
return {
  json: {
    mission_vision_plain: plain1,
    value_proposition_refined_plain: plain2,
    business_description_plain: plain3,
    core_problems_plain: plain4,
    id: $input.first().json.id
  }
};

