// Extract all internal links from homepage
const html = $input.first().json.data;
const baseUrl = $('Webhook').first().json.body['Website URL'];

function extractLinks(html, baseUrl) {
  const links = new Set();

  // --- Extract protocol and domain manually ---
  const protocol = baseUrl.startsWith('https') ? 'https:' : 'http:';
  const domain = baseUrl.replace(/^https?:\/\//, '').split('/')[0];

  // --- Regex to capture href links ---
  const linkRegex = /href\s*=\s*["']([^"']+)["']/gi;
  let match;

  while ((match = linkRegex.exec(html)) !== null) {
    let link = match[1].trim();

    // Skip unwanted types
    if (!link || link.startsWith('#') || link.startsWith('javascript:') || 
        link.startsWith('mailto:') || link.startsWith('tel:')) continue;

    // Handle relative links
    if (link.startsWith('/')) {
      link = `${protocol}//${domain}${link}`;
    } else if (!/^https?:\/\//i.test(link)) {
      // Handle relative paths like "about-us"
      link = `${protocol}//${domain}/${link}`;
    }

    // --- Check if it's internal (same domain) ---
    const host = link.replace(/^https?:\/\//, '').split('/')[0];
    if (host === domain) {
      links.add(link);
    }
  }

  // --- Prioritize important pages first ---
  const allLinks = Array.from(links);
  const priorityKeywords = /product|service|about|business|sector|solution|offer|contact|blog|playbook|pricing|case|portfolio/i;
  const priorityLinks = allLinks.filter(l => priorityKeywords.test(l));

  // Combine priority + others (limit 10)
  const finalLinks = [...priorityLinks, ...allLinks.filter(l => !priorityLinks.includes(l))].slice(0, 10);

  return finalLinks;
}

const links = extractLinks(html, baseUrl);
return links.map(link => ({ json: { url: link } }));

// Clean and extract text from all crawled pages
const items = $input.all();
let allText = '';

function cleanHTML(html) {
  return html
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

items.forEach(item => {
  if (item.json.data) {
    allText += cleanHTML(item.json.data) + '\n\n';
  }
});

// Add homepage text
const homepage = $('Fetch Homepage').first().json.data;
allText = cleanHTML(homepage) + '\n\n' + allText;

return [{ json: { 
  fullText: allText,
  websiteUrl: $('Webhook').first().json.body['Website URL']
}}];

// Parse seed keywords into array
const output = $json.output;
const keywords = output
  .split('\n')
  .map(k => k.replace(/^[\d\.\-\*#]+\s*/g, '').trim())
  .filter(k => k.length > 2 && k.length < 100)
  .map(k => k.toLowerCase());

const unique = [...new Set(keywords)];

return unique.slice(0, 50).map(kw => ({
  json: {
    keyword: kw,
    source: 'seed',
    websiteUrl: $('Webhook').first().json.body['Website URL']
  }
}));

// Deduplicate and prepare for enrichment
const items = $input.all();
const keywordMap = new Map();

items.forEach(item => {
  const kw = item.json.keyword?.toLowerCase().trim();
  if (kw && kw.length > 2 && kw.length < 100) {
    if (!keywordMap.has(kw)) {
      keywordMap.set(kw, {
        keyword: kw,
        source: item.json.source || 'seed'
      });
    }
  }
});

const unique = Array.from(keywordMap.values());
console.log(`Total unique keywords: ${unique.length}`);

return unique.map(kw => ({ json: kw }));

const response = $json;
const keywords = $('Deduplicate Keywords').all();
const results = [];

if (response.tasks && response.tasks.length > 0) {
  const task = response.tasks[0];

  // If the API returned valid results
  if (task.result && Array.isArray(task.result)) {
    task.result.forEach((item, idx) => {
      const k = keywords[idx]?.json?.keyword || 'unknown';
      results.push({
        keyword: k,
        search_volume: item.search_volume || 0,
        keyword_difficulty: item.keyword.length || 50,
        competition: item.competition || 'N/A',
        cpc: item.cpc || 0,
        source: keywords[idx]?.json?.source || 'N/A',
        status_message: task.status_message || 'OK'
      });
    });
  } else {
    // No valid results, log the error
    results.push({
      keyword: keywords.map(k => k.json.keyword).join(', '),
      search_volume: 0,
      keyword_difficulty: 0,
      competition: 'N/A',
      cpc: 0,
      source: 'error',
      status_message: task.status_message || 'No result returned'
    });
  }
}

return results.map(r => ({ json: r }));

// Filter out zero-volume keywords
const items = $input.all();

const filtered = items.filter(item => {
  const vol = item.json.search_volume;
  return vol && vol > 0;
});

console.log(`Filtered: ${filtered.length} keywords with volume > 0`);

return filtered;

// Collect all categorization outputs
const inputs = $input.all();
let mergedCategorization = [];

// Parse each output safely (remove markdown fences if present)
for (const item of inputs) {
  let text = item.json.output?.trim() || '';
  text = text.replace(/^```json|```$/g, '').trim(); // remove ```json and ending ```
  text = text.replace(/^```|```$/g, '').trim();     // remove any leftover ```

  try {
    const parsed = JSON.parse(text);
    if (Array.isArray(parsed)) mergedCategorization = mergedCategorization.concat(parsed);
  } catch (e) {
    console.log('âŒ Skipped invalid JSON block:', e.message);
  }
}

// Get keyword metrics data
const metricsData = $('Filter Zero Volume').all();

// Merge categorization with metrics
const enriched = metricsData.map(item => {
  const kw = item.json.keyword;
  const cat = mergedCategorization.find(c => c.keyword === kw) || {};

  return {
    json: {
      keyword: kw,
      pillar: cat.pillar || 'Uncategorized',
      cluster: cat.cluster || 'General',
      intent: cat.intent || 'Informational',
      search_volume: item.json.search_volume,
      keyword_difficulty: item.json.keyword_difficulty,
      source_type: item.json.source,
      competition: item.json.competition,
      cpc: item.json.cpc
    }
  };
});

return enriched;

// Calculate priority score and add recommendations
const items = $input.all();

const scored = items.map(item => {
  const vol = item.json.search_volume || 0;
  const kd = item.json.keyword_difficulty || 50;

  // Priority formula: (Volume / 100) * (100 - KD) / 50
  // Balances volume with achievability
  const priorityScore = Math.round((vol / 100) * ((100 - kd) / 50) * 10) / 10;

  // Generate content angle
  let contentAngle = '';
  const intent = item.json.intent;

  if (intent === 'Informational') {
    contentAngle = 'Blog post, guide, or how-to article';
  } else if (intent === 'Commercial') {
    contentAngle = 'Comparison page, product review, or buyer guide';
  } else if (intent === 'Transactional') {
    contentAngle = 'Product/service page with clear CTA';
  } else {
    contentAngle = 'Brand/navigation page';
  }

  return {
    json: {
      ...item.json,
      priority_score: priorityScore,
      notes: contentAngle
    }
  };
});

// Sort by priority score descending
scored.sort((a, b) => b.json.priority_score - a.json.priority_score);

console.log(`Total keywords with scores: ${scored.length}`);

return scored;

// Generate summary data for additional tabs
const allKeywords = $('Calculate Priority').all();

// Top by Pillar (top 15 per pillar)
const pillars = {};
allKeywords.forEach(item => {
  const pillar = item.json.pillar;
  if (!pillars[pillar]) pillars[pillar] = [];
  pillars[pillar].push(item.json);
});

const topByPillar = [];
Object.keys(pillars).forEach(pillar => {
  pillars[pillar]
    .sort((a, b) => b.priority_score - a.priority_score)
    .slice(0, 15)
    .forEach(kw => topByPillar.push(kw));
});

// Quick Wins (high volume, low/medium KD)
const quickWins = allKeywords
  .filter(item => {
    const vol = item.json.search_volume;
    const kd = item.json.keyword_difficulty;
    return vol >= 500 && kd <= 40;
  })
  .map(item => item.json)
  .slice(0, 50);

// Content Map
const contentMap = [];
Object.keys(pillars).forEach(pillar => {
  const clusters = {};
  pillars[pillar].forEach(kw => {
    const cluster = kw.cluster;
    if (!clusters[cluster]) {
      clusters[cluster] = {
        pillar,
        cluster,
        keyword_count: 0,
        avg_volume: 0,
        suggested_page_type: ''
      };
    }
    clusters[cluster].keyword_count++;
    clusters[cluster].avg_volume += kw.search_volume;
  });

  Object.values(clusters).forEach(cl => {
    cl.avg_volume = Math.round(cl.avg_volume / cl.keyword_count);
    cl.suggested_page_type = cl.keyword_count > 10 ? 'Hub Page' : 'Single Article';
    contentMap.push(cl);
  });
});

return [{
  json: {
    topByPillar,
    quickWins,
    contentMap,
    spreadsheetId: $('Write All Keywords').first().json.spreadsheetId,
    spreadsheetUrl: $('Write All Keywords').first().json.spreadsheetUrl
  }
}];

const items = $input.all();
const cleaned = [];
const seen = new Set();

for (const item of items) {
  let keyword = item.json.keyword?.toString().trim() || '';

  // Remove unwanted characters: keep Unicode letters, numbers, spaces, hyphens, and apostrophes
  keyword = keyword.replace(/[^\p{L}\p{N}\s'-]/gu, '').replace(/\s+/g, ' ').trim();

  // Skip if empty or too long
  if (!keyword || keyword.length > 45) continue;

  // Skip duplicates
  if (seen.has(keyword)) continue;
  seen.add(keyword);

  cleaned.push({ keyword, source: item.json.source || 'seed' });
}

// Create array of keyword strings
const keywordList = cleaned.map(i => i.keyword);

// Return a single JSON object containing both
return [
  {
    json: {
      cleaned,
      keywords: keywordList,
    },
  },
];

