  return [];
}

return data.map(item => ({ json: item }));

const data = $input.first()?.json?.quickWins;

if (!Array.isArray(data) || data.length === 0) {
  console.log("âš ï¸ No QuickWins data found or it's empty.");
  return [];
}

return data.map(item => ({ json: item }));

const data = $input.first()?.json?.contentMap;

if (!Array.isArray(data) || data.length === 0) {
  console.log("âš ï¸ No ContentMap data found or it's empty.");
  return [];
}

return data.map(item => ({ json: item }));

// Combine all keyword data into one item for the email
return [{
  json: {
    keywords: $input.all().map(item => item.json)
  }
}];
