const websiteUrl = $json['Website URL'];
const recipientEmail = $json['Recipient Email'];
const instagramHandle = $json['Instagram Handle'] || null;
const twitterHandle = $json['Twitter Handle'] || null;
const linkedinPage = $json['LinkedIn Page URL'] || null;

if (!websiteUrl || !recipientEmail) {
  throw new Error('Website URL and Recipient Email are required');
}

return [{
  json: {
    website_url: websiteUrl,
    recipient_email: recipientEmail,
    instagram_handle: instagramHandle,
    twitter_handle: twitterHandle,
    linkedin_page: linkedinPage,
    workflow_start: new Date().toISOString()
  }
}];

const html = $json.data || '';

function cleanHTML(html) {
  return html
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim()
    .slice(0, 20000);
}

const cleanText = cleanHTML(html);
const prevData = $('Store Inputs').first().json;

return [{
  json: {
    ...prevData,
    website_content: cleanText
  }
}];

const response = $input.first().json.message.content;
const jsonMatch = response.match(/\{[\s\S]*\}/);
const brandProfile = jsonMatch ? JSON.parse(jsonMatch[0]) : {};
const prevData = $('Clean Website Content').first().json;

return [{
  json: {
    ...prevData,
    brand_profile: brandProfile
  }
}];

const instagramData = $input.all()?.map(item => item?.json) ?? [];

let instagramInsights = {
  profile_exists: false,
  follower_count: 0,
  following_count: 0,
  biography: "",
  post_count: 0,
  recent_hashtags: [],
  city_name: "",
  captions: []
};

if (Array.isArray(instagramData) && instagramData?.length > 0) {
  const profile = instagramData?.[0] ?? {};

  // Collect all hashtags
  let allHashtags = [];
  if (Array.isArray(profile?.latestPosts)) {
    allHashtags = profile?.latestPosts
      ?.flatMap(post => post?.hashtags ?? [])
      ?.map(tag => tag?.trim() ?? "")
      ?.filter(tag => tag?.length > 0) ?? [];

    allHashtags = [...new Set(allHashtags)];
  }

  // Collect all captions
  let allCaptions = [];
  if (Array.isArray(profile?.latestPosts)) {
    allCaptions = profile?.latestPosts
      ?.map(post => post?.caption?.trim() ?? "")
      ?.filter(caption => caption?.length > 0) ?? [];

    allCaptions = [...new Set(allCaptions)];
  }

  instagramInsights = {
    profile_exists: true,
    follower_count: profile?.followersCount ?? 0,
    following_count: profile?.followsCount ?? 0,
    post_count: profile?.postsCount ?? 0,
    recent_hashtags: allHashtags ?? [],
    biography: profile?.biography ?? "",
    city_name: profile?.businessAddress?.city_name ?? "",
    captions: allCaptions ?? []
  };
}

const prevData = $node["Check Instagram Handle"]?.json ?? {};

return [
  {
    json: {
      ...prevData,
      instagram_insights: instagramInsights
    }
  }
];

const twitterData = $input.all()?.map(item => item?.json) ?? [];

let twitterInsights = {
  tweets: []
};

// Check if input is a list of tweets
if (Array.isArray(twitterData) && twitterData?.length > 0) {
  // Extract all text fields into an array
  const tweets = twitterData
    ?.map(t => t?.text?.trim?.() ?? "")
    ?.filter(text => text?.length > 0) ?? [];

  // Assign to insights
  twitterInsights = {
    tweets: tweets ?? []
  };
}

const prevData = $node["Check Twitter Handle"]?.json ?? {};

return [
  {
    json: {
      ...prevData,
      twitter_insights: twitterInsights
    }
  }
];

const linkedinData = $input.all()?.map(item => item?.json) ?? [];

let linkedinInsights = {
  posts: []
};

// Check if input is a list of posts
if (Array.isArray(linkedinData) && linkedinData?.length > 0) {
  // Extract all text fields into an array
  const posts = linkedinData
    ?.map(t => t?.text?.trim?.() ?? "")
    ?.filter(text => text?.length > 0) ?? [];

  // Assign to insights
  linkedinInsights = {
    posts: posts ?? []
  };
}

const prevData = $node["Check LinkedIn Page"]?.json ?? {};

return [
  {
    json: {
      ...prevData,
      linkedin_insights: linkedinInsights
    }
  }
];

const data = $input.first().json;
const currentDate = new Date();
const seasonalEvents = [];
const month = currentDate.getMonth();

if (month === 11 || month === 0) {
  seasonalEvents.push('Holiday Season', 'New Year Planning', 'Q4 Wrap-up');
} else if (month >= 9 && month <= 10) {
  seasonalEvents.push('Q4 Planning', 'Holiday Prep', 'Black Friday');
} else if (month >= 1 && month <= 2) {
  seasonalEvents.push('Q1 Goals', 'New Year Resolutions', 'Spring Planning');
} else if (month >= 3 && month <= 5) {
  seasonalEvents.push('Spring Refresh', 'Mid-year Planning', 'Summer Prep');
} else {
  seasonalEvents.push('Summer Season', 'Back to School', 'Q3 Reviews');
}

const industry = data.brand_profile.industry || '';
const trendingTopics = [
  `${industry} trends 2025`,
  `${industry} best practices`,
  `${industry} innovation`
];

const allHashtags = [];
if (data.instagram_insights && data.instagram_insights.recent_hashtags) {
  allHashtags.push(...data.instagram_insights.recent_hashtags);
}
if (data.twitter_insights && data.twitter_insights.recent_hashtags) {
  allHashtags.push(...data.twitter_insights.recent_hashtags);
}

const industryClean = industry.toLowerCase().replace(/\s+/g, '');
const defaultHashtags = [
  `#${industryClean}`,
  '#business',
  '#marketing',
  '#entrepreneur',
  '#innovation'
];

const relevantHashtags = allHashtags.length > 0 ? 
  [...new Set(allHashtags)].slice(0, 15) : defaultHashtags;

return [{
  json: {
    ...data,
    trending_topics: trendingTopics,
    seasonal_events: seasonalEvents,
    relevant_hashtags: relevantHashtags
  }
}];

