const response = $input.first().json.message.content;
const jsonMatch = response.match(/\{[\s\S]*\}/);
const pillarsData = jsonMatch ? JSON.parse(jsonMatch[0]) : {pillars:[]};
const prevData = $('Aggregate Market Context').first().json;

const ctaLibrary = {
  Awareness: ['Follow for insights', 'Save this', 'Share with network'],
  Consideration: ['Read full guide', 'Download resource', 'Join webinar'],
  Conversion: ['Book demo', 'Start trial', 'Sign up today']
};

return [{
  json: {
    ...prevData,
    content_pillars: pillarsData.pillars,
    cta_library: ctaLibrary
  }
}];

const data = $input.first().json;
const weekDates = [];
const currentDate = new Date();

for (let i = 0; i < 7; i++) {
  const date = new Date(currentDate);
  date.setDate(date.getDate() + i);
  const dayNames = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
  weekDates.push({
    day: dayNames[date.getDay()],
    date: date.toISOString().split('T')[0]
  });
}

const platforms = [];
if (data.instagram_handle) platforms.push('Instagram');
if (data.twitter_handle) platforms.push('Twitter');
if (data.linkedin_page) platforms.push('LinkedIn');

if (platforms.length === 0) {
  throw new Error('Provide at least one social platform');
}

const postGrid = [];
weekDates.forEach(dateObj => {
  platforms.forEach(platform => {
    postGrid.push({day: dateObj.day, date: dateObj.date, platform});
  });
});

const pillars = data.content_pillars.map(p => p.name);
const totalPosts = postGrid.length;

const funnelStages = [];
for(let i=0; i<Math.round(totalPosts*0.5); i++) funnelStages.push('Awareness');
for(let i=0; i<Math.round(totalPosts*0.3); i++) funnelStages.push('Consideration');
while(funnelStages.length < totalPosts) funnelStages.push('Conversion');

for(let i=funnelStages.length-1; i>0; i--) {
  const j = Math.floor(Math.random()*(i+1));
  [funnelStages[i], funnelStages[j]] = [funnelStages[j], funnelStages[i]];
}

const enrichedGrid = postGrid.map((post, idx) => ({
  ...post,
  pillar: pillars[idx % pillars.length],
  funnel_stage: funnelStages[idx]
}));

return [{
  json: {
    ...data,
    week_dates: weekDates,
    platforms,
    post_grid: enrichedGrid
  }
}];

