const response = $input.first().json.message?.content || "";

// Try to extract JSON array from markdown code block or inline
let jsonMatch = response.match(/```json([\s\S]*?)```/);
if (!jsonMatch) {
  jsonMatch = response.match(/\[[\s\S]*\]/);
}

let concepts = [];
if (jsonMatch) {
  let jsonText = jsonMatch[1] || jsonMatch[0];

  // Clean up common formatting issues
  jsonText = jsonText
    .replace(/â€œ|â€/g, '"')              // Replace curly quotes
    .replace(/,\s*}/g, '}')           // Trailing commas before object end
    .replace(/,\s*]/g, ']')           // Trailing commas before array end
    .replace(/(\w)"(\w)/g, '$1\\"$2') // Escaped quotes inside words (rare)

  try {
    concepts = JSON.parse(jsonText);
  } catch (err) {
    console.warn("JSON parsing failed:", err.message);
    concepts = [];
  }
}

const prevData = $('Plan Week Calendar').first().json;
const postGrid = prevData.post_grid || [];

const enrichedPosts = postGrid.map((post, idx) => {
  const concept = concepts[idx] || {
    concept: "Post",
    hook: "Default Hook",
    visual_idea: "Default Visual Idea"
  };

  const ctas = prevData.cta_library?.[post.funnel_stage] || [];
  const cta = ctas.length ? ctas[Math.floor(Math.random() * ctas.length)] : "Learn more";

  return { ...post, ...concept, cta };
});

return [{
  json: {
    ...prevData,
    content_calendar: enrichedPosts
  }
}];

